mapper.select:
select
tran.id, user.name as owner, tran.money, tran.name, tran.expectedDate, cus.name as customerId, tran.stage, tran.type, tran.source, tran.activityId, con.fullname as contactsId, tran.createBy, tran.createTime, tran.editBy, tran.editTime, tran.description, tran.contactSummary, tran.nextContactTime
from tbl_tran tran join tbl_user user on tran.owner=user.id join tbl_contacts con on tran.contactsId=con.id join tbl_customer cus on tran.customerId=cus.id
user.name like '%' #{owner} '%'
and tran.name like '%' #{name} '%'
and cus.name like '%' #{customerName} '%'
and tran.stage = #{stage}
and tran.type = #{type}
and tran.source = #{source}
and con.fullname like '%' #{contactsName} '%'
order by tran.createTime desc
limit #{skipCount},#{pageSize}
